function rot13(str) { // LBH QVQ VG!
  str=str.toUpperCase();
//create array of character codes for each character
var numeric=[];
for(var i=0; i<str.length; i++){
    numeric.push(str.charCodeAt(i));
}

//create array of new character codes for each letter
var decode=[];
for(var i=0;i<numeric.length; i++){
    //first half of alphabet adds 13
    if(numeric[i]>=65&&numeric[i]<=77){
        decode.push(numeric[i]+13);
    }
    //second half of alphabet subtracts 13
    else if (numeric[i]>=78 && numeric[i]<=90){
        decode.push(numeric[i]-13);
    }
    //special characters or spaces
    else{
        decode.push(numeric[i]);
    }
}
//create decoded string
decode=decode.map((x)=>String.fromCharCode(x)).join("");


console.log(numeric);
console.log(decode);
  return decode;
}

