function convertToRoman(num) {
var thous=Math.floor(num/1000);
var hund=Math.floor((num%1000)/100);
var ten=Math.floor((num%100)/10);
var one=num%10;
var romanNum="";

 //function to be used for hund, ten, and one - example: val=one, valOne=I, valFive=V, valTen=X
 var convert =function (val, valOne, valFive, valTen){
   if (val<4){
    for (var i=0; i<val; i++){
      romanNum+=valOne;
    }
   }
    else if (val===4){
      romanNum+=valOne;
      romanNum+=valFive;
    }
    else if (val>=5 && val<9){
    romanNum+=valFive;
    for (var i=5; i<val; i++){
      romanNum+=valOne;
    }
    }
    else {
      romanNum+=valOne;
      romanNum+=valTen;
    }
   }


if (thous>0){
  for(var i=0; i<thous; i++){
    romanNum+="M";
  }
}

if (hund>0){
  convert(hund, "C", "D", "M");
}
if (ten>0){
  convert(ten, "X", "L", "C");
}
if (one>0){
  convert(one, "I", "V", "X");
}


console.log(thous, hund, ten, one);
console.log(romanNum);
 return romanNum;
}

convertToRoman(36);
convertToRoman(1234);
